SELECT 		CASE 			WHEN altproc.processual=1 THEN 'CONTENCIOSO' 			WHEN altproc.consultivo=1 THEN 'CONSULTIVO' 			WHEN altproc.procuracao=1 THEN 'PROCURACOES' 			WHEN altproc.contrato=1 THEN 'CONTRATOS' 			WHEN altproc.generico=1 THEN 'GENERICO' 		END AS MODULOS_RELACIONADOS, 		altproc.chaveproc AS CHAVEPROC, 		altproc.processo AS NUMERO, 		(SELECT Nome FROM altprof WHERE codprof=altproc.codprof) AS RESP, 		'MARCIA ANTONIETA CRUZ TRIGUEIRO OABMG 72859' AS RESPONSAVEL_PELO_FLUXO, 		altanda.texto AS JUSTIFICATIVA FROM altproc,ALTANDA 	WHERE 		altproc.chaveproc = altanda.chaveproc (+)		AND 0 = altanda.manual (+)  		AND altproc.apagado =  0 		AND altproc.chaveproc IN  			( 				SELECT  					chaveproc  				FROM  					altctrpruprof  				WHERE  					codprof = 19 					AND statusaprova = 0  					AND sequenciaaprovadores IN  						( 							SELECT  								MIN(sequenciaaprovadores) as sequenciaaprovadores  							FROM  								altctrpruprof  							WHERE  								altproc.chaveproc=altctrpruprof.chaveproc  								AND statusaprova = 0 						) 			) ;